package com.lutfullayevmuhammad.mydictionary.core.di.componentsimport android.app.Applicationimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectionModuleimport dagger.android.AndroidInjectorimport dagger.android.support.DaggerApplicationimport com.lutfullayevmuhammad.mydictionary.core.di.module.app.AppModuleimport com.lutfullayevmuhammad.mydictionary.core.di.module.room.RoomModuleimport com.lutfullayevmuhammad.mydictionary.core.di.ui.MainActivityModuleimport javax.inject.Singleton@Singleton@Component(    modules = [        AndroidInjectionModule::class,        AppModule::class,        RoomModule::class,        MainActivityModule::class    ])interface AppComponent : AndroidInjector<DaggerApplication> {    override fun inject(instance: DaggerApplication)    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application): Builder        fun build(): AppComponent    }}