package com.lutfullayevmuhammad.mydictionary.core.di.uiimport com.lutfullayevmuhammad.mydictionary.MainActivityimport com.lutfullayevmuhammad.mydictionary.RecActivityimport com.lutfullayevmuhammad.mydictionary.UserActivityimport dagger.Moduleimport dagger.android.ContributesAndroidInjector@Moduleabstract class MainActivityModule {    @ContributesAndroidInjector(        modules = [HomeModule::class]    )    abstract fun provideMainActivity(): MainActivity    @ContributesAndroidInjector(        modules = [HomeModule::class]    )    abstract fun provideRecActivity(): RecActivity    @ContributesAndroidInjector(        modules = [HomeModule::class]    )    abstract fun provideUserActivity(): UserActivity}