package com.lutfullayevmuhammad.mydictionary.core.models.dictionaryUserimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabase@Database(entities = [UserItem::class], version = 1, exportSchema = true)abstract class AppDb : RoomDatabase() {    abstract fun userDao(): UserDao    companion object {        private var db_instance: AppDb? = null        fun getAppDbInstance(context: Context): AppDb {            if (db_instance == null) {                db_instance = Room.databaseBuilder(                    context.applicationContext,                    AppDb::class.java,                    "user_db"                )                    .allowMainThreadQueries()                    .build()            }            return db_instance!!        }    }}