package com.lutfullayevmuhammad.mydictionary.core.models.dictionaryUserimport androidx.room.*@Daointerface UserDao {    @Query("SELECT * from user_table")    fun getAllUsers(): List<UserItem>    @Insert(entity = UserItem::class, onConflict = OnConflictStrategy.REPLACE)    fun insert(vararg userItem: UserItem)    @Delete(entity = UserItem::class)    fun delete(userItem: UserItem)    @Update(entity = UserItem::class)    fun update(userItem: UserItem)}